body {
  margin: 0;
  background-color: #222;
  color: white;
  font-family: "Helvetica Neue", "Calibri Light", Roboto, sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  letter-spacing: 0.02em;
  font-size: 18px;
}

h1, h2, h3, h4, h5, h6, p {
  margin: 0;
}

a {
  color: lightblue;
}

#router-view {
  padding: 20px 20px;
}

.input-container {
  padding: 20px;
  color: #CCC;
}

